$(".panel__toggle").click(function() {
    const $this = $(this),
        p = $this.parent().next()
    if (($this).hasClass("toggle-on")) {
        $this.removeClass("toggle-on")
        $this.addClass("toggle-off")
        p.hide()
    } else {
        $this.removeClass("toggle-off")
        $this.addClass("toggle-on")
        p.show()
    }
})

